/** SE CREAN LAS TABLAS DE LA BASE DE DATOS**/

/* Crear la tabla de Clientes*/

CREATE TABLE Clientes(
	Cliente_ID int NOT NULL,	
	Nom_Cliente	varchar(80) NULL,
	Nom_Contacto varchar(80) NULL,
	Direccion	varchar(100) NULL,
	Ciudad	varchar(20) NULL,
	Codigo_Postal varchar(10) NULL,
	Pais varchar(20) NULL,
    CONSTRAINT [PK_Clientes] PRIMARY KEY (Cliente_ID ASC) 
)
GO

/* Crear la tabla de Categorias*/
CREATE TABLE Categorias(
	Categoria_ID	int NOT NULL,
	Nom_Categoria varchar(30) NULL,
	Descripcion varchar(200) NULL,
    CONSTRAINT [PK_Categorias] PRIMARY KEY (Categoria_ID ASC) 
)
GO

/* Crear la tabla de Proveedores*/
CREATE TABLE Proveedores(
	Proveedor_ID	int NOT NULL,
	Nom_Proveedor	varchar(80) NULL,
	Nom_Contacto	varchar(80) NULL,
	Direccion	varchar(100) NULL,
	Ciudad	varchar(20) NULL,
	Cod_Postal	varchar(10) NULL,
	Pais	varchar(20) NULL,
	Telefono varchar(20) NULL,
	CONSTRAINT [PK_Proveedores] PRIMARY KEY (Proveedor_ID ASC) 
)
GO

/* Crear la tabla de Transportistas*/
CREATE TABLE Transportistas(
	Transportista_ID	int NOT NULL,
	Nom_Transportista	varchar(20) NULL,
	Telefono	varchar(20) NULL,
	CONSTRAINT [PK_Transportistas] PRIMARY KEY (Transportista_ID ASC) 
)
GO

/* Crear la tabla de Empleados*/
CREATE TABLE Empleados(
	Empleado_ID	int NOT NULL,
	Nombre	varchar(20) NULL,
	Apellido	varchar(20) NULL,
	Fec_Nacimiento	datetime,
	CONSTRAINT [PK_Empleados] PRIMARY KEY (Empleado_ID ASC) 
)
GO

/* Crear la tabla de Productos*/
CREATE TABLE  Productos (
	Producto_ID int NOT NULL,
	Nom_Producto varchar(50) NULL,
	Proveedor_ID int NOT NULL,
	Categoria_ID int NOT NULL,
	Unidad varchar(50) NOT NULL,
	Precio Numeric(6,2) NOT NULL,
	CONSTRAINT [PK_Productos] PRIMARY KEY (Producto_ID ASC),
	CONSTRAINT FK_Productos_Proveedores FOREIGN KEY (Proveedor_ID) REFERENCES Proveedores(Proveedor_ID),
	CONSTRAINT FK_Productos_Categorias FOREIGN KEY (Categoria_ID) REFERENCES Categorias(Categoria_ID)
)
GO

/* Crear la tabla de Ordenes*/
CREATE TABLE  Ordenes (
	Orden_ID	 int NOT NULL,
	Cliente_ID	 int NOT NULL,
	Empleado_ID	 int NOT NULL,
	Fec_Orden	datetime,
	Transportista_ID  int NOT NULL,
	CONSTRAINT [PK_Ordenes] PRIMARY KEY (Orden_ID ASC),
	CONSTRAINT FK_Ordenes_Clientes FOREIGN KEY (Cliente_ID) REFERENCES Clientes(Cliente_ID),
	CONSTRAINT FK_Ordenes_Empleados FOREIGN KEY (Empleado_ID) REFERENCES Empleados(Empleado_ID),
	CONSTRAINT FK_Ordenes_Transportistas FOREIGN KEY (Transportista_ID) REFERENCES Transportistas(Transportista_ID)
)
GO

/* Crear la tabla de Ordenes*/
CREATE TABLE  Ordenes_Detalle (
	Orden_Detalle_ID	int NOT NULL,
	Orden_ID	int NOT NULL,
	Producto_ID	int NOT NULL,
	Cantidad int NOT NULL,
	CONSTRAINT [PK_Ordenes_Detalle] PRIMARY KEY (Orden_Detalle_ID ASC),
	CONSTRAINT FK_Ordenes_Detalle_Ordenes FOREIGN KEY (Orden_ID) REFERENCES Ordenes(Orden_ID),
	CONSTRAINT FK_Ordenes_Detalle_Productos FOREIGN KEY (Producto_ID) REFERENCES Productos(Producto_ID)
)
GO


